mapper.insert:
INSERT INTO product
    ( prodcode
    , prodname
    , price
    , quantity)
VALUES 
    ( #{prodCode}
     ,#{prodName}
     ,#{price}
     ,#{quantity} )

mapper.update:
UPDATE product p     
   SET p.prodname = #{prodName}
     , p.price    = #{price}
     , p.quantity = #{quantity}
     , p.moddate  = sysdate
 WHERE p.prodcode = #{prodCode}

mapper.select:
SELECT p.prodcode   
  FROM product p    
 WHERE p.prodcode = #{prodCode}
